DROP TABLE IF EXISTS "contacts";
CREATE TABLE "contacts" (
    "_id" integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	"sid" integer NOT NULL,
	"pid" integer NOT NULL,
	"ispeople" integer NOT NULL,
	"description" text NOT NULL DEFAULT '',
	"location" text NOT NULL DEFAULT '',
	"email" text NOT NULL DEFAULT '',
	"comment" text NOT NULL DEFAULT '',
	"phone" text NOT NULL DEFAULT '',
    CONSTRAINT "unique_sid" UNIQUE (sid)
);
CREATE INDEX "index_pid" ON contacts (pid ASC);


DROP TABLE IF EXISTS "favorite_contacts";
CREATE TABLE favorite_contacts(
    "_id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, 
    "contact_sid" integer, 
    "name" text,
	CONSTRAINT "foreign_sid" FOREIGN KEY ("contact_sid") REFERENCES "contacts" ("sid") ON DELETE CASCADE ON UPDATE CASCADE
);

DROP TABLE IF EXISTS "table_timestamp";
CREATE TABLE table_timestamp(
    "_id" integer NOT NULL PRIMARY KEY AUTOINCREMENT,
    "name" text NOT NULL,
    "timestamp" text NOT NULL,
    "localstamp" integer NOT NULL
);
